mapper.insert:
insert into reserve (reserve_id, isbn, reader_id, 
  reader_identity, reserve_time, status
  )
values (#{reserveId,jdbcType=INTEGER}, #{isbn,jdbcType=VARCHAR}, #{readerId,jdbcType=VARCHAR}, 
  #{readerIdentity,jdbcType=INTEGER}, #{reserveTime,jdbcType=TIMESTAMP}, #{status,jdbcType=INTEGER}
  )

mapper.select:
select

from reserve



    and isbn like concat('%',#{isbn},'%')


    and status = #{status}


    and reserve_id = #{reserveId}


    and reader_identity = #{readerIdentity}


    and reader_id = #{readerId }
